	/** 
	 * 在岗学生信息修改
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 * @throws Exception 
	 */
	public ActionForward modiZgxsgwxx(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws Exception{
		QgzxForm model = (QgzxForm) form;
		QgzxGwglService service = new QgzxGwglService();
		String dir = "/upload/qgzx";
		File f = new File(dir);
	    if (!f.exists()) {
		   f.mkdir();
	    }
	    
	    FormFile file = model.getUploadFile();
	    if(file!=null && StringUtils.isNotNull(file.getFileName())){
	    	int size = file.getFileSize();
		    if(size<10485760){
			   String fName = file.getFileName();
			   InputStream in = file.getInputStream();
			   String filePath = dir + "/" + fName;
			   OutputStream out = new FileOutputStream(filePath);
			   int bytesRead = 0;
			   byte[] buffer = new byte[size];
			   while ((bytesRead = in.read(buffer, 0, size)) != -1) {
				   out.write(buffer, 0, bytesRead);
			   }
			   out.close();
			   in.close();
		    }else{
		    	request.setAttribute("result", "文件过大,操作失败！");
		    	return mapping.findForward("modiXszgxx");
		    }
	    }
	    model.setFjwjmc(file.getFileName());
	    model.setPkValue(request.getParameter("pkV"));
	    request.setAttribute("result", service.modifyXsgwxxb(model,request)==false ? "操作失败！" : "操作成功！");
		return mapping.findForward("modiXszgxx");
	}
	
	/** 
	 * 文件下载
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 * @throws Exception 
	 */
	public ActionForward viewFile(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws Exception{
		String fileName = request.getParameter("fileName");
		String dir = "/upload/qgzx/"+fileName;
		byte b[] = new byte[500];
		File fileload = new File(dir);
		
		response.setContentType("application/octet-stream");
		response.setHeader("Content-Disposition", "attachment;filename="
				+ DealString.toUtf8String(fileName));
		InputStream in = new FileInputStream(fileload);
		in = new BufferedInputStream(in);
		while ((in.read(b)) != -1) {
			response.getOutputStream().write(b);
		}
		return mapping.findForward("");
	}
	
