	/**
	 * 批量增加学生工作记录信息
	 * @param ActionMapping mapping
	 * @param ActionForm form
	 * @param HttpServletRequest request
	 * @param HttpServletResponse response
	 * @return ActionForward
	 * @throws Exception 
	 * */
	public ActionForward addXsgzjlBatch(ActionMapping mapping, ActionForm form,HttpServletRequest request,
			HttpServletResponse response) throws Exception{
		QgzxForm model = (QgzxForm) form;
		QgzxXskhService service = new QgzxXskhService();
		XsxxglService xsxxglService = new XsxxglService();
		QgzxService qgzxService = new QgzxService();
		String xh = model.getXh();
		String xmdm = DealString.toGBK(model.getXmdm());
		model.setGwdm(xmdm.split("-")[0]);
		model.setGwsbsj(xmdm.split("-")[1]);
		
		List<String[]> list = new ArrayList<String[]>();
		
		String zgzsj = "";
		for(int i=1; i<32; i++){
			zgzsj = request.getParameter("zgzsj" + i);
			if(!StringUtils.isNull(zgzsj)){
				String[] values = new String[5];
				values[0] = i+""; 
				values[1] = StringUtils.isNull(request.getParameter("gzkssj" + i)) ? "" : request.getParameter("gzkssj" + i);
				values[2] = StringUtils.isNull(request.getParameter("gzjssj" + i)) ? ""  : request.getParameter("gzjssj" + i);
				values[3] = StringUtils.isNull(request.getParameter("zgzsj" + i)) ? "" : request.getParameter("zgzsj" + i);
				values[4] = StringUtils.isNull(request.getParameter("gznr" + i)) ? "" : request.getParameter("gznr" + i);
				list.add(values);
			}
		}
		
		request.setAttribute("result", service.saveXsgzjlBatch(model,list));
		request.setAttribute("rs", xsxxglService.selectStuinfo(xh));
		request.setAttribute("gwList", service.getGwList(xh));
		request.setAttribute("ndList", Base.getXnndList());//学年年度列表
		request.setAttribute("yfList", qgzxService.getYfList());
		request.setAttribute("rqList", qgzxService.getRqList());
		return mapping.findForward("xsgzjlAddBatch");
	}
	
