	/**
	 * 打印学生工作考核表
	 * @param ActionMapping mapping
	 * @param ActionForm form
	 * @param HttpServletRequest request
	 * @param HttpServletResponse response
	 * @return ActionForward
	 * @throws Exception 
	 * */
	public ActionForward printXskhb(ActionMapping mapping, ActionForm form,HttpServletRequest request,
			HttpServletResponse response) throws Exception{
		QgzxXskhService service = new QgzxXskhService();
		String pkValue = request.getParameter("pkValue");
		String modelPath = servlet.getServletContext().getRealPath("")+"/print/qgzx/xsqgzxkhb.xls";
		response.reset();
		response.setContentType("application/vnd.ms-excel");
		WritableWorkbook wwb = ExcelMethods.getWritableWorkbook(new File(modelPath), response.getOutputStream());
		
		service.printXskhb(wwb,pkValue);
		return mapping.findForward("");
	}
	
