public class WhlgdxGwglAction extends DispatchAction {
	/** 
	 * 保存岗位信息
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward addGwxx(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
			QgzxService qgzxService = new QgzxService();
			CommanForm model = (CommanForm) form;
			WhlgdxGwglService service = new WhlgdxGwglService();
			String writeAble =  CheckPower.checkUsrPower(request.getSession().getAttribute("userName").toString(),
					            "comm_pub.do?doType=add&tableName=view_gwxx")==true ? "yes" : "no" ;
			String pkValue = "";
			String knsbl = request.getParameter("knsbl");
			String xueqi = model.getXueqi();
			//操作类型参数
			String doType = request.getParameter("doType");
			boolean flag = true;
			if(doType!=null && doType.equalsIgnoreCase("add")){
				//增加
				flag = service.addGwxx(model,request);
				request.setAttribute("result", flag);				
			}
			if(doType!=null && doType.equalsIgnoreCase("modi")){
				//修改
				pkValue = request.getParameter("pkValue");
				flag = service.modGwxx(pkValue,model,request);
				request.setAttribute("result", flag);
			}
			model = new CommanForm();
			model.setKnsbl(knsbl);
			model.setXueqi(xueqi);
			request.setAttribute("rs", model);
			request.setAttribute("doType", doType);
			request.setAttribute("pkValue", pkValue);
			request.setAttribute("writeAble", writeAble);
			//校区
			request.setAttribute("xqList1", qgzxService.getXiaoquList());
			//岗位性质
			request.setAttribute("gwxzList", qgzxService.getGwxzList());
			//申请单位
			request.setAttribute("sqdwList", qgzxService.getYrdwList());
			//信用度
			request.setAttribute("xydList", qgzxService.getXydList());
			//专业
			request.setAttribute("zydmList", Base.getZyMap().get(""));			
			return mapping.findForward("success");
	}
	
	/** 
	 * 学生查询岗位详细信息
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward showGwxxDetail(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
//			CommanForm model = (CommanForm) form;
			WhlgdxGwglService service = new WhlgdxGwglService();
			String pkValue =DealString.toGBK(request.getParameter("pkValue"));
			
			request.setAttribute("rs", service.getGwxxDetail(pkValue));
			return new ActionForward("/qgzx/whlgdx/gwxx_detail.jsp",false);
	}	
	
	/** 
	 * 学校审核酬金发放
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward cjffAudit(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
			WhlgdxGwglService service = new WhlgdxGwglService();
			QgzxCjffService cjffService = new QgzxCjffService();
			
			QgzxService qgzxService = new QgzxService();
			String page = "/qgzx/whlgdx/work_pay_details.jsp";
			String xxdm = StandardOperation.getXxdm();
			String pkValue = DealString.toGBK(request.getParameter("pkValue"));
			
			List stuList = service.getCjffInfoOfStu(pkValue);
			if(xxdm.equalsIgnoreCase(Globals.XXDM_ZGDZDX)){
				//中国地质大学
				page = "/qgzx/zgdzdx/work_pay_auditing.jsp";
				stuList = cjffService.getCjffInfoOfStu(pkValue);				
			}
			
			request.setAttribute("map", qgzxService.getSqsjInfo());
			request.setAttribute("rs", service.getCjffInfo(pkValue));
			request.setAttribute("count", stuList.size());
			request.setAttribute("stuList", stuList);
			return new ActionForward(page);
	}
	
	/** 
	 * 学校审核酬金发放
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward saveAudit(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		HttpSession session = request.getSession();
		String pkValue = DealString.toGBK(request.getParameter("pkValue"));
		WhlgdxGwglService service = new WhlgdxGwglService();
		String userType = session.getAttribute("userType").toString();
		
		boolean flag = false;
		//学校审核
		if(userType!=null && (userType.equalsIgnoreCase("xx")|| userType.equalsIgnoreCase("admin"))){			
			flag = service.saveAudit(pkValue,request);
		}
		request.setAttribute("result", flag);
		return this.cjffAudit(mapping, form, request, response);
	}	
}