public class RcsqXfcjAction extends DispatchAction {

	/** 
	 * 学费催缴中获取学费缓交信息
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward getHjInfo(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		RcswXfcjService service = new RcswXfcjService();
		RcswXfcjDAO dao = new RcswXfcjDAO();
		String xh = request.getParameter("xh");
		//页面要显示的主键值
		String pkValue = request.getParameter("pkValue");
		HashMap<String, String> rs = service.getXfhjInfo(xh); 
		
		request.setAttribute("rs", rs);
		request.setAttribute("ndList", Base.getXnndList());
		request.setAttribute("xnList", Base.getXnndList());
		request.setAttribute("xqList", Base.getXqList());
		request.setAttribute("qflxList", dao.getQflxList()); //向页面发送欠费类型列表
		request.setAttribute("doType", "add");//只是在增加的时候去取学费缓交信息
		request.setAttribute("pkValue", pkValue);
		
		//返回到/sjcz/xfcjb.jsp
		return mapping.findForward("success");
	}
}