public class XjydglAction extends BasicAction {
	
	/**
	 * 导出学籍异动信息
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 * @throws Exception 
	 * @throws IOException 
	 * **/
	public ActionForward expXjydxx(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws IOException, Exception {
		HttpSession session = request.getSession();
		StudentInfoForm model = (StudentInfoForm) form;
		XjydglService service = new XjydglService();
		String[] outputColumn = {"xh","xm","xb","lxdh","sfzx","clwh","ydxh","ydrq",
				                 "ydhbjdm","ydhbjmc","ydhnj","ydhxjztm","ydhxydm",
				                 "ydhxymc","ydhxz","ydhzydm","ydhzymc","ydlbdm",
				                 "ydlbmc","ydqbjdm","ydqbjmc","ydqnj","ydqxjztm",
				                 "ydqxydm","ydqxymc","ydqxz","ydqzydm","ydqzymc",
				                 "ydsm","ydyy","ztmc"};
		if(session.getAttribute("fdyQx").equals(true)){
			model.setIsFdy("true");
			model.setUserName(session.getAttribute("userName").toString());
		}else if("xy".equalsIgnoreCase(session.getAttribute("userType").toString())){
			model.setYdhxydm(session.getAttribute("userDep").toString());
		}
		
		List<String[]> list = (List<String[]>)service.queryXjydxx("view_xjydjbxx", model, outputColumn);
		String[] colListCN = service.getColumnComment("view_xjydjbxx", outputColumn);
		
		response.reset();
		response.setContentType("application/vnd.ms-excel");		
		Excel2Oracle.exportData(list,outputColumn,colListCN, response.getOutputStream());
		return mapping.findForward("");
	}
	
}