/*判断是否为数字*/
function isNumber(str) {
	var Letters = "1234567890";
	for (var i = 0; i < str.length; i = i + 1) {
		var CheckChar = str.charAt(i);
		if (Letters.indexOf(CheckChar) == -1) {
			return false;
		}
	}
	return true;
}

