function mustFill(str){
	var strsp = str.split("-");
	for(var i=0;i<strsp.length; i++){
		var documentE = document.getElementById(strsp[i]).value;
		if(documentE == null || documentE == ""){	
			alert("请将带*号的文本框填写号");
			 return false;
		}
	}
	return true;
}

